import { ProfileApi } from "./ProfileApi.js";

export class UserReference {
    /**
     * @param id {string}
     * @param profileApi {ProfileApi}
     */
    constructor(id, profileApi) {
        this.id = id
        this.profileApi = profileApi
    }

    /**
     * Get this user's public profile
     * @returns {Promise<{firstName?: string, lastName?: string, country?: {alpha2: string, name: string}, email?: {value: string, confirmed: boolean}, kycVerified?: boolean, state?: string, profilePicture?: string}>}     */
    async profile() {
        return await this.profileApi.info(this.id)
    }
}