export class OrganizationRole {
    /**
     * An object that contains information about the user's role in the organization
     * @param api {OrganizationApi}
     * @param role {{name: string, id: string}}
     * @param member {OrganizationMembership}
     */
    constructor(api, member, role) {
        this.api = api
        this.id = role.id
        this.name = role.name
        this.member = member
    }

    /**
     * Remove this role from the member
     * @returns {Promise<boolean>}
     */
    async remove() {
        try {
            const response = await this.api.roleDelete(this.member.organizationId, this.member.membershipId, this.id)
            return response.success;

        } catch {
            return false
        }
    }
}